mapper.select:
select count(1)
from t_record
where user_id = #{userId}
limit #{start}, #{pageSize}

select id, user_id userId, latitude, longitude, survey_time surveyTime, land_msg landMsg, crop_type cropType, crop_variety cropVariety, sp_canopy_img spCanopyImg, note, create_time createTime, update_time updateTime
from t_record
where user_id = #{userId}
limit #{start}, #{pageSize}

select id, user_id userId, latitude, longitude, survey_time surveyTime, land_msg landMsg, crop_type cropType, crop_variety cropVariety, sp_canopy_img spCanopyImg, note, create_time createTime, update_time updateTime
from t_record
where id = #{recordId}

select id, record_id recordId, latitude, longitude, disease_type diseaseType, survey_leaves surveyLeaves, diseased_leaves diseasedLeaves, quadrat_canopy_img quadratCanopyImg, quadrat_leaves_img quadratleavesImg, create_time createTime, update_time updateTime
from t_disease_sample_coll
where record_id = #{recordId}

select id, record_id recordId, latitude, longitude, disease_type diseaseType, survey_leaves surveyLeaves, diseased_leaves diseasedLeaves, quadrat_canopy_img quadratCanopyImg, quadrat_leaves_img quadratLeavesImg, create_time createTime, update_time updateTime
from t_disease_sys_survey
where record_id = #{recordId}

select id, record_id recordId, latitude, longitude, disease_type diseaseType, survey_leaves surveyLeaves, diseased_leaves diseasedLeaves, site_canopy_img siteCanopyImg, create_time createTime, update_time updateTime
from t_disease_data_coll_nav
where record_id = #{recordId}

select id, record_id recordId, latitude, longitude, pest_type pestType, quadrat_canopy_img quadratCanopyImg, create_time createTime, update_time updateTime
from t_pest_collection
where record_id = #{recordId}

select img
from t_pest_img
where pest_coll_record_id = #{pestCollRecordId}

select id, record_id recordId, latitude, longitude, pest_type pestType, quadrat_canopy_img quadratCanopyImg, create_time createTime, update_time updateTime
from t_pest_survey_uav
