select img
from t_pest_uav_img
where pest_survey_uav_record_id = #{pestSurveyUAVRecordId}

select id, record_id recordId, latitude, longitude, soil_moisture soilMoisture, soil_temp soilTemp, air_moisture airMoisture, air_temp airTemp, create_time createTime, update_time updateTime
from t_environment_factor
